'use strict';

import q from 'q';
import BaseProvision from '../provision/BaseProvision';


/**
 * This is a base object that contains all you can do about Bundles.
 */
export default class AIModels extends BaseProvision {

    /**     
     * @param {InternalOpenGateAPI} Reference to the API object.
     */
    constructor(ogapi) {
        super(ogapi, "/ai", undefined, ["organization", "file"], 'north/ai');
        this._ogapi = ogapi;
    }

    _buildURL() {
        var url = this._organization + '/models/' + this._identifier;
        return url;
    }

    /**
     * Set the identifier attribute
     * @param {string} identifier
     * @return {Transformers}
     */
    withIdentifier(identifier) {
            if (typeof identifier !== 'string' || identifier.length > 50)
                throw new Error({ message: "OGAPI_STRING_PARAMETER_MAX_LENGTH_50", parameter: 'identifier' });
            this._identifier = identifier;
            return this;
        }

    /**
     * Set the organization attribute
     * @param {string} organization 
     * @return {Transformers}
     */
    withOrganization(organization) {
        if (typeof organization !== 'string' || organization.length > 50)
            throw new Error({ message: "OGAPI_STRING_PARAMETER_MAX_LENGTH_50", parameter: 'organization' });
        this._organization = organization;
        return this;
    }

    withFile(file) {
        // if (typeof file !== 'object')
        //     throw new Error({ message: "Parameter action requires name and type", parameter: 'action' });
        this._file = file
    }

    _composeElement() {
        this._checkRequiredParameters();
        this._resource = this._organization + '/models';

        var transformer = {
            modelFile:  this._file || undefined
        };
        return transformer;
    }

    _composeUpdateElement() {
        let model = super._composeUpdateElement();
        return model;
    }

    prediction(body_data) {
        var defered = q.defer();
        var promise = defered.promise;

        //En muchas clases se genera this._resource en la llamada a la funcion this._composeElement()

        this._ogapi.Napi.post(this._buildURL() + '/prediction', body_data, this._timeout, this._getExtraHeaders(), this._getUrlParameters(), this._getServiceBaseURL())
            .then((res) => {
                defered.resolve({
                    statusCode: res.statusCode,
                    body: res.body
                });
            })
            .catch((error) => {
                defered.reject(error);
            });
        return promise;
    }

    create() {
        let _postElement = this._composeElement();
        
        const defer = q.defer();
        
        this._ogapi.Napi.post_multipart(this._resource, {'modelFile': _postElement.modelFile}, {}, this._timeout, this._getExtraHeaders(), this._getUrlParameters(), this._getServiceBaseURL())
            .then((response) => {
                let statusCode = response.statusCode;
                switch (statusCode) {
                    case 200:{
                        const resultQuery = response.text != "" ? JSON.parse(response.text) : {};
                        const _statusCode = response.status;
                        defer.resolve({
                            data: resultQuery,
                            statusCode: _statusCode
                        });
                        break
                    }
                    case 201:{
                        const _statusCode = response.status;
                        const location =  response.header && response.header.location
                        defer.resolve({
                            location: location,
                            statusCode: _statusCode
                        });
                        break
                    }
                    case 204:
                        defer.resolve(response);
                        break
                    default:
                        defer.reject({
                            errors: response.data.errors,
                            statusCode: response.statusCode
                        });
                        break
                }
            })
            .catch((error) => {
                defer.reject(error);
            });
        return defer.promise;
    }

    update() {
        let _postElement = this._composeElement();

        const defer = q.defer();
        
        this._ogapi.Napi.put_multipart( this._buildURL(), {'modelFile': _postElement.modelFile}, {}, this._timeout, this._getExtraHeaders(), this._getUrlParameters(), this._getServiceBaseURL())
            .then((response) => {
                let statusCode = response.statusCode;
                switch (statusCode) {
                    case 200:{
                        const resultQuery = response.text != "" ? JSON.parse(response.text) : {};
                        const _statusCode = response.status;
                        defer.resolve({
                            data: resultQuery,
                            statusCode: _statusCode
                        });
                        break
                    }
                    default:
                        defer.reject({
                            errors: response.data.errors,
                            statusCode: response.statusCode
                        });
                        break
                }
            })
            .catch((error) => {
                defer.reject(error);
            });
        return defer.promise;
    }
}

'use strict';

import GenericFinder from '../GenericFinder';

/**
 *   This class allow make get request to aiModel resource into Opengate North API.
 */
export default class AIModelsFinder extends GenericFinder {

    /**     
     * @param {InternalOpenGateAPI} ogapi - Reference to the API object.
     */
    constructor(ogapi) {
        super(ogapi, 'ai', 'list', 'AIModel not found', 'north');
    }

     /**
     * Download a complete list of aiModels by its organization. This execute a GET http method
     * @test
     *   ogapi.newAIModelFinder().findByOrganization('orgname').then().catch();
     * @param {string} organization - aiModel organization .
     * @return {Promise} 
     */
    findByOrganization(organization) {
        this._organization = organization;
        return this._execute();
    }

    /**
     * Gets a specific ai model by its organization and id. This execute a GET http method
     * @test
     *   ogapi.newAIModelFinder().findByOrganizationAndIdentifier('orgname', xxx-xx-xxx-xxx').then().catch();
     * @param {string} organization - transformer organization .
     * @param {string} identifier - transformer identifier.
     * @return {Promise} 
     */
     findByOrganizationAndIdentifier(organization, identifier) {
        this._organization = organization;
        this._identifier = identifier;
        return this._execute();
    }

    /**
     * Download a specific ai model by its organization and id. This execute a GET http method
     * @test
     *   ogapi.newAIModelFinder().downloadByOrganizationAndIdentifier('orgname', xxx-xx-xxx-xxx').then().catch();
     * @param {string} organization - transformer organization .
     * @param {string} identifier - transformer identifier.
     * @return {Promise} 
     */
    downloadByOrganizationAndIdentifier(organization, identifier) {
        this._organization = organization;
        this._identifier = identifier;
        this._filename = true
        return this._download();
    }

    /**
     * @return {String} This returns a string with the URL of the request.
     * @private
     */
    _composeUrl() {
        return this._baseUrl + "/" + this._organization + "/models" +(this._identifier?'/'+ this._identifier + (this._filename?'/file':''):'');
    }
}

'use strict';

import q from 'q';
import HttpStatus from 'http-status-codes';

/**
 *   This class allow make get request to user resource into Opengate North API.
 */
export default class GenericFinder {

    /**     
     * @param {InternalOpenGateAPI} ogapi - Reference to the API object.
     * @param {string} source - Relative url where is located the resource.
     * @param {string} reponseJsonData - Relative url where is located the resource. Can be null
     * @param {string} error_not_found - String error which will be thrown on not_found error.
     * @param {string} serviceBaseURL - base of the uri petition
     */
    constructor(ogapi, source, entity, error_not_found, serviceBaseURL) {
        this._api = ogapi.Napi;
        this._apiSouth = ogapi.Sapi;
        this._baseUrl = source;
        this._entity = entity;
        this._error_not_found = error_not_found;
        this._id = undefined;
        this._headers = undefined;
        this._urlParameters = undefined;
        this._serviceBaseURL = serviceBaseURL;
    }

    /**
     * @return {String} This returns a string with the URL of the request.
     * @private
     */
    _composeUrl() {
        return this._baseUrl + "/" + this._id;
    }

    _withId(_id) {
        this._id = _id;
        return this;
    }

    _getExtraHeaders() {
        return this._headers;
    }

    _setExtraHeaders(headers) {
        if (this._headers) {
            var keys = Object.keys(headers);
            for (var i = 0; i < keys.length; i++) {
                var key = keys[i];
                this._headers[key] = headers[key];
            }
        } else {
            this._headers = headers;
        }
    }

    _getUrlParameters() {
        return this._urlParameters;
    }

    _setUrlParameters(parameters) {
        if (this.parameters) {
            var keys = Object.keys(parameters);
            for (var i = 0; i < keys.length; i++) {
                var key = keys[i];
                this._urlParameters[key] = parameters[key];
            }
        } else {
            this._urlParameters = parameters;
        }
    }

    /**
     * @return {Promise}
     * @private
     */
    _execute(south) {
        let _api = this._api
        if(south){
            _api = this._apiSouth
        }
        let defered = q.defer();
        let promise = defered.promise;
        let _entity = this._entity;
        let _error_not_found = this._error_not_found;
        this._api.get(this._composeUrl(), undefined, this._getExtraHeaders(), this._getUrlParameters(), false, this._getServiceBaseURL())
            .then((req) => {
                if (req.statusCode === 204) {
                    defered.reject({
                        error: _error_not_found,
                        statusCode: HttpStatus.NOT_FOUND
                    });
                } else {
                    if (req.body) {
                        var data = req.body[_entity] && req.body.provision ? req.body : req.body[_entity];
                        defered.resolve({
                            data: data ? data : req.body,
                            statusCode: req.statusCode
                        });
                    } else {
                        defered.resolve({
                            data: req.text,
                            statusCode: req.statusCode
                        });
                    }

                    // }
                }
            })
            .catch((error) => {
                // BUG RELACIONADO (http://cm.amplia.es/jira/browse/OGODM-3250)
                if (error.statusCode === 400) {
                    error.statusCode = HttpStatus.NOT_FOUND;
                }

                defered.reject(error);
            });
        return promise;
    }

    _downloadUrl() {
        return this._composeUrl();
    }

    /**
     * @return {Promise}* @private
     */
     _download(noBlob) {
        let defered = q.defer();
        let promise = defered.promise;
        let _error_not_found = this._error_not_found;
        this._api.get(this._downloadUrl(), undefined, this._getExtraHeaders(), this._getUrlParameters(), (noBlob?false:true), this._getServiceBaseURL())
            .then((req) => {
                if (req.statusCode === 204) {
                    defered.reject({
                        data: _error_not_found,
                        statusCode: HttpStatus.NOT_FOUND
                    });
                } else {
                    defered.resolve({
                        data: req,
                        statusCode: req.statusCode
                    });
                }
            })
            .catch((error) => {
                defered.reject(error);
            });
        return promise;
    }

    _getServiceBaseURL() {
        return this._serviceBaseURL;
    }

}

