     * @example
     *   ogapi.newOperationFinder().findPeriodicityByPeriodicityId('xxx-xx-xxx-xxx').then().catch();
     * @param {string} periodicityId - Periodicity id.
     * @return {Promise} 
     */
    findPeriodicityByPeriodicityId(periodicityId) {
        var _this = this;
        let defered = q.defer();
        let promise = defered.promise;
        _this._id = periodicityId;
        _this._baseUrl = 'operation/tasks';
        _this._entity = "task";
        _this._execute()
            .then(function (response) {
                response.data.id = _this._id;
                defered.resolve(response);
            })
            .catch(function (error) {
                defered.reject(error);
            });
        return promise;
    }

    /**
     * Download a specific executions of an operation by its id. This execute a GET http method
     * @test
     *   ogapi.newOperationFinder().findExecutionsById('xxx-xx-xxx-xxx').then().catch();
     *   ogapi.newOperationFinder().findExecutionsById('xxx-xx-xxx-xxx', 10).then().catch(); // Without offset
     *   ogapi.newOperationFinder().findExecutionsById('xxx-xx-xxx-xxx', 20, 50).then().catch(); //With offset value 50
     * @param {string} id - Operation id.
     * @param {number} size - Defined the number of elements on response
     * @param {number} [start=0] - Defined the offset on response
     * @return {Promise} 
     */
    findExecutionsById(id, size = LIMIT_SIZE_DEF_VALUE, start = LIMIT_START_DEF_VALUE) {
        this._id = id;
        this._baseUrl = 'operation/jobs';
        this._entity = 'operations';
        this._error_not_found = 'Executions not found';
        if (typeof size !== "number") throw new Error('size parameter must be a number');
        if (typeof start !== "number" || start < 1)
            start = LIMIT_START_DEF_VALUE;
        this._limit = {
            size: size,
            start: start
        };
        return this._execute();
    }

    /**
     * @return {String} This returns a string with the URL of the request.
     * @private
     */
    _composeUrl() {
        if (this._entity === 'operations') {
            if (this._limit) {
                this._setUrlParameters({
                    start: this._limit.start,
                    size: this._limit.size
                });
            }
            var base_url = this._baseUrl + "/" + this._id + "/operations";
            return base_url;
        }
        return this._baseUrl + "/" + this._id;
    }

}

'use strict';

import BaseOperationBuilder from './catalog/BaseOperationBuilder';
import PeriodicityUpdateBuilder from './catalog/period/PeriodicityUpdateBuilder';
import q from 'q';
import merge from 'merge';

/**
 * This class generates all operations builders by a response to search into catalog/operations
 */
export default class Operations {
    /**
     * @param {!InternalOpenGateAPI} ogapi - this is configuration about Opengate North API.
     */
    constructor(ogapi) {
        this._ogapi = ogapi;
        this._operations = [];
        this._operationNames = [];
    }

    /**	
     * Create a builder to update the periodicity of an operation 
     * @param {!String} operationId - identifier of the operation to be updated~
     * @return {Promise}
     */
    updatePeriodicityBuilder(operationId) {
        if (typeof operationId !== "string") {
            throw new Error({ message: "OGAPI_STRING_PARAMETER", parameter: "operationId" });
        }
        let defered = q.defer();
        let promise = defered.promise;
        let _this = this;
        this._ogapi.newOperationFinder().findPeriodicityById(operationId)
            .then(function(response) {
                defered.resolve(_this._createPeriodicBuilder(response.data));
            })
            .catch(function(error) {
                defered.reject(error);
            });
        return promise;
    }

    /**	
     * Create a builder to create an operation 
     * @param {!String} name - name of the operation to be created
     * @return {Promise}
     */
    builderByOperationName(name) {
        if (typeof name !== "string") {
            throw new Error({ message: "OGAPI_STRING_PARAMETER", parameter: "name" });
        }
        let defered = q.defer();
        let promise = defered.promise;
        let opN = name.toUpperCase();
        let op = this._findOperation(opN);
        let _this = this;
        if (typeof op === "undefined") {
            this._loadOperationCatalog().then(function() {
                let op = _this._findOperation(opN);
                if (typeof op === "undefined") {
                    defered.reject("Operation <'" + opN + "'> unavailable.");
                } else {
                    defered.resolve(_this._createBuilder(op.config));
                }
            });
        } else {
            defered.resolve(this._createBuilder(op.config));
        }
        return promise;
    }

    getOperationList() {
        return merge(true, this._operationNames);
    }
    
    _createBuilder(config) {
        return new BaseOperationBuilder(this._ogapi, config);
    }

    _createPeriodicBuilder(task) {
        return new PeriodicityUpdateBuilder(this._ogapi, task.id, task);
    }

    _loadOperationCatalog() {
        let _this = this;
        let defered = q.defer();
        let promise = defered.promise;
        this._ogapi.operationTypesSearchBuilder().build().execute().
        then(function(data) {
            if (data.statusCode === 200) {
                const operations = data.data;
                for (let i in operations) {
                    let name = operations[i].name.toUpperCase();
                    _this._operationNames.push(name);
                    _this._operations.push({ name: name, config: operations[i] });
                }
            }
            defered.resolve(_this);
        }).catch(function(err) {
            defered.resolve(_this);
        });
        return promise;
    }

    _findOperation(name) {
        return this._operations.find(function(config) {
            return config.name == this;
        }, name);
    }
}

'use strict';

import q from 'q';

export default class PeriodicityActions {

    /**
     * @param {InternalOpenGateAPI} ogapi - this is configuration about Opengate North API.
     * @param {string} taskId - Identifier of the periodicity on which the action will be carried out
     */
    constructor(ogapi, taskId) {
        if (typeof taskId !== 'string')
            throw new Error('Parameter taskId must be a string');
        this._ogapi = ogapi;
        this._resource = 'operation/tasks/';
        this._taskId = taskId;
        this._key = 'task';
    }

    /**
     * This invoke a request to OpenGate North API and the callback is managed by promises
     * This function active periodicity of an operation
     * @example 
     * ogapi.newPeriodicityActions("xxxxx-xxx-xxxx-xxxxx").activate()
     * @return {Promise} 
     * @property {function (statusCode:number)} then - When request it is OK
     * @property {function (errors:array, statusCode:number)} then - When request it is NOK
     * @property {function (errors:array)} catch - When request it is NOK
     */
    activate() {
        let config = {
            active: true
        };
        return this._periodicityActions("ACTIVE", config);
    }

    /**
     * This invoke a request to OpenGate North API and the callback is managed by promises
     * This function pauses a periodicity
     * @example 
     * ogapi.periodicityActions("xxxxx-xxx-xxxx-xxxxx").pausePeriodicity()
     * @return {Promise} 
     * @property {function (statusCode:number)} then - When request it is OK
     * @property {function (errors:array, statusCode:number)} then - When request it is NOK
     * @property {function (errors:array)} catch - When request it is NOK
     */
    pause() {
        let config = {
            active: false
        };
        return this._periodicityActions("PAUSE", config);
    }

    /**
     * This invoke a request to OpenGate North API and the callback is managed by promises
     * This function cancel a periodicity
     * @return {Promise}
     * @property {function (result:object, statusCode:number)} then - When request it is OK
     * @property {function (errors:array, statusCode:number)} then - When request it is NOK
     * @property {function (errors:array)} catch - When request it is NOK
     * @example
     * ogapi.periodicityActions("xxxxx-xxx-xxxx-xxxxx").cancelPeriodicity();
     */
    cancel() {
        return this._periodicityActions("CANCEL");
    }

    _cancel() {
        var defered = q.defer();
        var promise = defered.promise;
        this._ogapi.Napi.delete(this._resource)
            .then((response) => {
                if (response.statusCode === 200) {
                    defered.resolve({ statusCode: response.statusCode, data: { id: this._id } });
                } else {
                    defered.reject({ errors: response.errors, statusCode: response.statusCode });
                }
            })
            .catch((error) => {
                defered.reject(this._formatError(error));
            });
        return promise;
    }

    _periodicityActions(action, config) {
        var _this = this;
        var defered = q.defer();
        var promise = defered.promise;
        _this._ogapi.newOperationFinder().findPeriodicityByPeriodicityId(_this._taskId)
            .then(function(response) {
                var data = response.data;
                if (!data || Object.keys(data).length == 0) {
                    defered.reject("Periodicity with id " + _this._taskId + " not exists");
                } else {
                    let periodicityId = data.id;
                    _this._resource = _this._resource + periodicityId;
                    switch (action) {
                        case "PAUSE":
                        case "ACTIVE":
                            _this._update(config)
                                .then(function(response) {
                                    defered.resolve(response);
                                }).catch(function(error) {
                                    defered.reject(error);
                                });
                            break;
                        case "CANCEL":
                            _this._id = periodicityId;
                            _this._cancel()
                                .then(function(response) {
                                    defered.resolve(response);
                                }).catch(function(error) {
                                    defered.reject(error);
                                });
                            break;
                        default:
                            defered.reject({ errors: "Not implemented action: " + action });
                    }
                }
            })
            .catch(function(error) {
                defered.reject(error);
            });
        return promise;
    }

    /**
     * This invoke a request to OpenGate North API and the callback is managed by promises
     * This function read and update a task
     * @example 
     * ogapi.periodicityActions("xxxxx-xxx-xxxx-xxxxx")._readAndUpdate({})
     * @param {object} - config 
     * @param {boolean} - forceToActivate
     * @return {Promise} 
     * @property {function (statusCode:number)} then - When request it is OK
     * @property {function (errors:array, statusCode:number)} then - When request it is NOK
     * @property {function (error:string)} catch - When request it is NOK
     */
    _readAndUpdate(config, forceToActivate) {
        let defered = q.defer();
        let promise = defered.promise;
        var _this = this;
        _this._ogapi.newOperationFinder().findPeriodicityByPeriodicityId(_this._taskId).then(
            function(response) {
                var data = response.data;
                if (!data) {
                    defered.reject("Periodicity with id " + _this._taskId + " not exists");
                } else {
                    let active = data.task ? data.task.state === 'ACTIVE' : false;
                    if (active) {
                        _this.pause().then(
                            function(response) {
                                _this._update(config, forceToActivate || active).then(
                                    function(response) {
                                        defered.resolve(response);
                                    }
                                ).catch(
                                    function(error) {
                                        defered.reject(_this._formatError(error));
                                    }
                                );
                            }
                        ).catch(
                            function(error) {
                                defered.reject(_this._formatError(error));
                            }
                        );

                    } else {
                        _this._update(config, forceToActivate || active).then(
                            function(response) {
                                defered.resolve(response);
                            }
                        ).catch(
                            function(error) {
                                defered.reject(_this._formatError(error));
                            }
                        );
                    }
                }
            }
        ).catch(
            function(error) {
                defered.reject(_this._formatError(error));
            }
        );

        return promise;
    }

    /**
     * This invoke a request to OpenGate North API and the callback is managed by promises
     * This function update an operation
     * @example 
     * ogapi.periodicityActions("xxxxx-xxx-xxxx-xxxxx")._update({})
     * ogapi.periodicityActions("xxxxx-xxx-xxxx-xxxxx")._update({}, _defered, _promise)
     * @return {Promise} 
     * @property {function (statusCode:number)} then - When request it is OK
     * @property {function (errors:array, statusCode:number)} then - When request it is NOK
     * @property {function (error:string)} catch - When request it is NOK
     */
    _update(config, forceToActivate) {
        var _this = this;
        let defered = q.defer();
        let promise = defered.promise;
        let obj = {};
        obj[_this._key] = config;
        this._ogapi.Napi
            .put(this._resource, obj)
            .then((response) => {
                if (forceToActivate) {
                    _this.active().then(
                        function(response) {
                            defered.resolve(response);
                        }
                    ).catch(
                        function(error) {
                            defered.reject(this._formatError(error));
                        }
                    );
                } else {
                    if (response.status === 200) {
                        defered.resolve(response);
                    } else {
                        defered.reject({ errors: response.errors, statusCode: response.status });
                    }
                }
            })
            .catch((error) => {
                defered.reject(this._formatError(error));
            });
        return promise;
    }

    _formatError(error) {
        if (!error.data) {
            error.data = {};
        }
        if (!error.data.errors) {
            error.data.errors = [(typeof(error) === "string") ? { message: error } : error];
        }
        return error;
    }


}

'use strict';

/**
* Util used into BaseOperationBuilder to append entities the three different ways. By filter, By tags, By entityList
*/
export default class AppendEntitiesBy {
	/**
	* @param {!InternalOpenGateAPI} ogapi - this is configuration about Opengate North API.
	* @param {!BaseOperationBuilder} parent - this is a instance of BaseOperationBuilder
	*/	
	constructor(ogapi,parent){
		this._ogapi = ogapi;
		this._parent = parent;
	}

	/**
	* Append filter to operation target
	* @param {!FilterBuilder} filter 
	* @param {!string} entityType 
	* @return {BaseOperationBuilder}
	*/
	filter(filter,entityType){
		let entityTypeFound = this._parent._config.applicableTo.find(function(type){return type==this;},entityType);
		if (typeof entityTypeFound === "undefined"){
			throw new Error("Entity type <'"+entityType+"'> not allowed to operation <'"+
				this._parent._config.name+"'>. Entity types allowed <'"+
				JSON.stringify(this._parent._config.applicableTo)+"'>");
		}
		this._parent._entityTypeWhenFilter = entityType;
		if (typeof this._parent._build.target !== "undefined") 
			console.warn("An Operation only allow one kind of way to append entities. "+
			"Filter | Tag | List of entities. Now Filter will remove the last way appended .");			
		if (typeof filter._filterTemplate !== "undefined"){
			this._parent._build.target = {
				filter:filter._filterTemplate.filter
			};
		}else{
			this._parent._build.target = {
				filter:filter
			};
		}
		return this._parent;
	}
	
	/**
	* Append entity list to operation target
	* @param {!EntityListBuilder} entities 
	* @return {BaseOperationBuilder}
	*/
	list(entities){
		if (typeof entities === "undefined" || entities.constructor !== Array){
			throw new Error("Parameter entities must be typeof Array");
		}
		if (typeof this._parent._build.target !== "undefined") 
			console.warn("An Operation only allow one kind of way to append entities. "+
			"Filter | Tag | List of entities. Now  List of entities will remove the last way appended .");
		

		this._parent._build.target = {
			append:{}
		};

		entities.forEach((entity) => {
			if (entity.constructor === String) {
				if (!this._parent._build.target.append.entities) {
					this._parent._build.target.append.entities = [];
				}
				this._parent._build.target.append.entities.push(entity);
			} else {
				if (entity.id && entity.parameters) {
					if (!this._parent._build.target.append.entitiesWithParameters) {
						this._parent._build.target.append.entitiesWithParameters = [];
					}
					this._parent._build.target.append.entitiesWithParameters.push(entity);
				} else {
					throw new Error("Entity parameters must include id and parameters: " + JSON.stringify(entity));
				}
			}
		})
		return this._parent;
	}
	
	/**
	* Set tag to operation target
	* @param {!string} tag 
	* @return {BaseOperationBuilder}
	*/
	tag(tag){
		if (typeof this._parent._build.target !== "undefined") 
			console.warn("An Operation only allow one kind of way to append entities. "+
			"Filter | Tag | List of entities. Now Tag will remove the last way appended .");
		this._parent._build.target = {
			append:{
				tags:[tag]
			}
		};
		return this._parent;
	}
}

'use strict';

import merge from 'merge';

import Operation from './Operation';

import ParameterBuilderFactory from './parameters/ParameterBuilderFactory';
import AppendEntitiesBy from './AppendEntitiesBy';
import ExecuteEachBuilder from './period/ExecuteEachBuilder';
import ExecuteEveryBuilder from './period/ExecuteEveryBuilder';

import moment from 'moment';
import { TIME_FORMAT, DATE_FORMAT } from './../../util/DATE_FORMAT';

import Ajv from 'ajv'

const DEFAULT_DELAYED_STOP = 43200; //Valor por defecto, 43200 minutos, equivale a un mes de retraso. Conclusión a la que se ha llegado mediante inspiración divina.
const ACK_TIMEOUT = "ackTimeout",
    TIMEOUT = "timeout",
    RETRIES = "retries",
    RETRIES_DELAY = "retriesDelay";
const RETRY_RESULT_LIST = "retryResultList";
const VALIDATE = {
    gte: function(value) {
        if (value < this)
            throw new Error("Value expected must be greater than <" + this + ">. Value setted <" + value + ">");
    },
    list: function(value) {
        let valueFound = this.find(function(value) {
            return value == this;
        }, value);
        if (typeof valueFound === "undefined")
            throw new Error("Value must be one of these: " + JSON.stringify(this));
    },
    editable: function(value) {
        return true;
        // Desactivada comprobación, es incoherente el valor en el catalogo de operaciones.
        /*if (!this)
            throw new Error("This parameter cannot be edited.");*/
    }
};

/**
 * Defines the builder to execute an operation that is into catalog
 */
export default class BaseOperationBuilder {
    /**
     * @param {!InternalOpenGateAPI} ogapi - this is configuration about Opengate North API.
     * @param {!object} config - this is configuration about operation. 
     */
    constructor(ogapi, config) {
        this._ajv = new Ajv({useDefaults: "empty", coerceTypes: true})
        // this._requiredParameters = [];
        /**
         * Util used into BaseOperationBuilder to append entities the three different ways. By filter, By tags, By entityList
         */
        this.appendEntitiesBy = new AppendEntitiesBy(ogapi, this);
        this._config = config;
        this._ogapi = ogapi;
        this._resourcesAvailables = {
            job: '/jobs',
            task: '/tasks'
        };
        this._entityTypeWhenFilter = undefined;
        this._build = {
            operationParameters: {
                ackTimeout: 0,
                timeout: 90000,
                retries: 0,
                retriesDelay: 0,
                retryResultList:[]
            },
            name: config.name,
            schedule: {}
        };
        //if (typeof config.parameters !== "undefined" && config.parameters.length > 0) {
        if (typeof config.parameters !== "undefined") {
            /**
             * This class contains all operation parameters builders
             */
            // this.paramBuilderFactory = new ParameterBuilderFactory(ogapi, config.parameters, this);
            this._build.parameters = {};
            // for (let i = 0; i < config.parameters.length; i++) {
            //     let param = config.parameters[i];
            //     if (param.required === true) {
            //         this._requiredParameters.push(param.name);
            //     }
            // }
        }
    }


    /**
     * Set notes to operation
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().withNotes("own notes")
     * @param {!string} notes - If null then parameter will be removed into builder
     * @throws {Error} throw error when notes is not typeof string
     * @return {BaseOperationBuilder}
     */
    withNotes(notes) {
            if (notes === null) {
                delete this._build.userNotes;
                return this;
            }
            if (typeof notes !== "string")
                throw new Error('Parameter notes must be a string');
            this._build.userNotes = notes;
            return this;
        }
        /**
         * Set a callback to operation. If it is set also will be set notify with true value
         * @example
         *  ogapi.operations.builderFactory.newXXXBuilder().withCallback("http://my.web")
         * @param {string} url -  If null then parameter will be removed into builder
         * @throws {Error} throw error when url is not typeof string
         * @return {BaseOperationBuilder}
         */
    withCallback(url) {
        if (url === null) {
            delete this._build.callback;
            delete this._build.notify;
            return this;
        }

        if (typeof url !== "string")
            throw new Error('Parameter url must be a string');
        this._build.callback = url;
        this._build.notify = true;
        return this;
    }

    /**
     * Set a scattering max spread to operation.
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().withScatteringMaxSpread(20)
     * @param {number} percentage - if null then parameter will be removed into builder         
     * @throws {Error} throw error when percentage is not typeof number
     * @throws {Error} throw error when percentage is greater than 100 and less than 0  
     * @return {BaseOperationBuilder}
     */
    withScatteringMaxSpread(percentage) {
        if (percentage === null && typeof this._build.schedule.scattering !== "undefined") {
            delete this._build.schedule.scattering.maxSpread;
            return this;
        }
        if (typeof percentage !== "number") {
            throw new Error("Parameter percentage must be a number");
        }
        if (percentage < 0 || percentage > 100) {
            throw new Error("The value of percentage parameter must be between 0-100");
        }
        if (typeof this._build.schedule.scattering === "undefined")
            this._build.schedule.scattering = {};
        this._build.schedule.scattering.maxSpread = percentage;
        return this;
    }

    /**
     * Set a scattering strategy to operation.
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().withScatteringStrategy(20,4)
     * @param {number} factor - if null then parameter will be removed into builder         
     * @param {number} warningMaxRate           
     * @throws {Error} throw error when factor is not typeof number
     * @throws {Error} throw error when factor is greater than 100 and less than 0  
     * @return {BaseOperationBuilder}
     */
    withScatteringStrategy(factor, warningMaxRate) {
        if (factor === null && typeof this._build.schedule.scattering !== "undefined") {
            delete this._build.schedule.scattering.strategy;
            return this;
        }

        if (typeof factor !== "number") {
            throw new Error("Parameter factor must be a number");
        }
        if (factor < 0 || factor > 100) {
            throw new Error("The value of factor parameter must be between 0-100");
        }

        if (typeof this._build.schedule.scattering === "undefined")
            this._build.schedule.scattering = {};

        this._build.schedule.scattering.strategy = {
            field: "subscription.collected.cellInfo",
            factor: factor
        };

        if (typeof warningMaxRate === "number") {
            this._build.schedule.scattering.strategy.warningMaxRate = warningMaxRate;
        }

        return this;
    }

    /**
     * The operation will be execute immediately.
     * @return {BaseOperationBuilder}
     */
    executeImmediately() {
        this._build.active = true;
        if (typeof this._build.schedule !== "undefined") {
            delete this._build.schedule.start;
        }
        delete this._build.task;
        return this;
    }

    /**
     * The operation will be created in IDLE state
     * @return {BaseOperationBuilder}
     */
    executeIDLE() {
        throw new Error("Not implemented yet");
    }

    /**
     * The operation will be created with delayed start or if you not pass any argument then the method return a cron expression builder.
     * @param {!number} minutes
     * @param {boolean} active - If active is false, an operation is created in paused
     * @throws {Error} throw error when minutes is not typeof number
     * @return {BaseOperationBuilder|CronExpressionBuilder} 
     */
    executeLater(minutes, active = true) {
        if (typeof minutes !== "number") {
            throw new Error("Parameter minutes must be typeof number");
        }
        this._build.active = active;
        if (typeof this._build.schedule === "undefined") {
            this._build.schedule = {};
        }
        this._build.schedule.start = {
            delayed: moment.duration(minutes, 'minutes').asMilliseconds()
        };
        delete this._build.task;
        return this;
    }

    /**
     * The operation will execute with a period that you must define with ExecuteEveryBuilder 
     * @param {!Date} date - Date when operation will be executed
     * @param {string} name - Name associated to periodicity
     * @param {number or Date} end - When periodicity ends. By repetitions or by date
     * @param {boolean} active - If active is false, an operation is created in paused
     * @param {string} description - Description associated to periodicity
     * @throws {Error} throw error when date is not typeof Date
     * @return {ExecuteEveryBuilder}
     */
    executeEvery(date, name, end, active = true, description) {
        if (typeof date === "undefined" || date.constructor !== Date) {
            throw new Error("Parameter date must be typeof Date");
        }
        let args = Array.prototype.slice.call(arguments);
        let _name = this._getName(args.slice(1, 3));
        let _end = this._getEnd(args.slice(1, 3));
        this._build.active = active;
        return new ExecuteEveryBuilder(this, date, _name, _end, description);
    }

    /**
     * The operation will execute with a period that you must define with ExecuteEachBuilder 
     * @param {!Date} date - Date when operation will be executed
     * @param {string} name - Name associated to periodicity
     * @param {number or Date} end - When periodicity ends. By repetitions or by date   
     * @param {boolean} active - If active is false, an operation is created in paused
     * @param {string} description - Description associated to periodicity
     * @throws {Error} throw error when date is not typeof Date
     * @return {ExecuteEachBuilder}
     */
    executeEach(date, name, end, active = true, description) {
        if (typeof date === "undefined" || date.constructor !== Date) {
            throw new Error("Parameter date must be typeof Date");
        }
        let args = Array.prototype.slice.call(arguments);
        let _name = this._getName(args.slice(1, 3));
        let _end = this._getEnd(args.slice(1, 3));
        this._build.active = active;
        return new ExecuteEachBuilder(this, date, _name, _end, description);
    }

    _getName(args) {
        for (let i = 0; i < args.length; i++) {
            if (typeof args[i] === "string") {
                return args[i];
            }
        }
        return this._build.name + " " + this._ogapi.Napi._options.apiKey;
    }

    _getEnd(args) {
        for (let i = 0; i < args.length; i++) {
            if (typeof args[i] === "number" || (args[i] && args[i].constructor === Date)) {
                return args[i];
            }
        }
        return undefined;
    }

    /**
     * Set a timeout of job.
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().withJobTimeout(3)
     * @param {number} minutes - if null then parameter will be removed into builder
     * @throws {Error} throw error when minutes is not typeof number    
     * @return {BaseOperationBuilder}
     */
    withJobTimeout(minutes) {
        if (minutes === null) {
            delete this._build.schedule.stop;
            return this;
        }
        if (typeof minutes !== "number") {
            throw new Error("Parameter minutes must be a number");
        }
        this._build.schedule.stop = {
            delayed: moment.duration(minutes, 'minutes').asMilliseconds()
        };
        return this;
    }

    /**
     * Set ackTimeout to operation.
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().withAckTimeout(11)
     * @param {!number} milliseconds    
     * @param {string} format - Can be 'milliseconds' || 'ms' ,'seconds' || 's', 'minutes' || 'm', 'hours' || 'h', 'days' || 'd', 'weeks' || 'w', 'months' || 'M'
     * @throws {Error} throw error when milliseconds is not typeof number   
     * @return {BaseOperationBuilder}
     */
    withAckTimeout(milliseconds, format = "milliseconds") {
        this._addSpecificParameter(moment.duration(milliseconds, format).asMilliseconds(), ACK_TIMEOUT);
        return this;
    }

    /**
     * Set timeout to operation.
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().withTimeout(11)
     * @param {!number} milliseconds    
     * @param {string} format - Can be 'milliseconds' || 'ms' ,'seconds' || 's', 'minutes' || 'm', 'hours' || 'h', 'days' || 'd', 'weeks' || 'w', 'months' || 'M'
     * @throws {Error} throw error when milliseconds is not typeof number   
     * @return {BaseOperationBuilder}
     */
    withTimeout(milliseconds, format = "milliseconds") {
        this._addSpecificParameter(moment.duration(milliseconds, format).asMilliseconds(), TIMEOUT);
        return this;
    }

    /**
     * Set delay between operation retries.
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().withRetriesDelay(11)
     * @param {!number} milliseconds    
     * @param {string} format - Can be 'milliseconds' || 'ms' ,'seconds' || 's', 'minutes' || 'm', 'hours' || 'h', 'days' || 'd', 'weeks' || 'w', 'months' || 'M'
     * @throws {Error} throw error when milliseconds is not typeof number   
     * @return {BaseOperationBuilder}
     */
    withRetriesDelay(milliseconds, format = "milliseconds") {
        this._addSpecificParameter(moment.duration(milliseconds, format).asMilliseconds(), RETRIES_DELAY);
        return this;
    }

    /**
     * Set operation retries
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().withOperationRetries(11)
     * @param {Array} operationRetries    
     * @throws {Error} throw error when operationRetries is not typeof string   
     * @return {BaseOperationBuilder}
     */
    withOperationRetries(operationRetries) {
        this._addSpecificParameter(operationRetries, RETRY_RESULT_LIST);
        return this;
    }

    /**
     * Set number of retries that operation will have.
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().withRetries(2)
     * @param {!number} retriesNumber   
     * @throws {Error} throw error when retriesNumber is not typeof number  
     * @return {BaseOperationBuilder}
     */
    withRetries(retriesNumber) {
        this._addSpecificParameter(retriesNumber, RETRIES);
        return this;
    }

    /**
     * Set parameters of the operation
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().withParameters({ param1: 'value1', param2: 'value2'})
     * @param {!object} parameters   
     * @throws {Error} throw error when parameters is not typeof object  
     * @return {BaseOperationBuilder}
     */
    withParameters(parameters) {
        if (this._config.parameters) {
            this._build.parameters = parameters;
            this._checkMandatoryParameters();
            return this;
        } else {
            throw new Error('This operation does not support parameters')
        }
    }

    withParameter(parameter, value) {
        if (this._config.parameters) { 
            if ( !this._build.parameters) {
                this._build.parameters = {}
            }
            
            this._build.parameters[parameter] = value;
            return this;
        } else {
            throw new Error('This operation does not support parameters')
        }
    }

    /**
     * Build a instance of Operation 
     *
     * @example
     *  ogapi.operations.builderFactory.newXXXBuilder().build()
     * @throws {Error} Throw error if there are required parameters who have not been set
     * @return {Operation} 
     */
    build() {
        let resource;
        let _build = merge(true, this._build);
        let postObj;
        let errors = [];
        try {
            this._checkMandatoryParameters();
        } catch (err) {
            errors.push(err.message);
        }
        if (typeof this._build.task === "undefined") {
            if (typeof this._build.schedule.start === "undefined" && typeof this._build.active === "undefined") {
                console.info("Not specified the way to execute [executeImmediately, executeIDLE, executeLater]. By default executeImmediately will be the way");
                this.executeImmediately();
            }
            /*if (!this._build.active) {
                errors.push("INERR: OgAPI will not allowed to execute IDLE because there is not implemented the way to update once created ");
            }*/
        }
        if (typeof this._build.target === "undefined") {
            if (this._build.active) {
                errors.push("Must be entities appended  if you want execute immediately. You must invoke appendEntitiesBy.list or appendEntitiesBy.tags or appendEntitiesBy.filter");
            }
        }
        if (typeof this._build.target !== "undefined" && typeof this._build.target.filter !== "undefined") {
            if (typeof this._entityTypeWhenFilter !== "string") {
                errors.push("Must be selected the entity type allowed when filter is the way to append entities. Allowed entity types <'" +
                    JSON.stringify(this._config.applicableTo) + "'>");
            }
        }

        if (typeof this._build.task !== "undefined") {
            let task = this._build.task;
            // CHECK period and job timeout
            let jobTimeout = this._build.schedule.stop;
            if (typeof task.repeating.period !== "undefined") {
                let maxJobTimeout;
                switch (task.repeating.period.unit) {
                    case "DAYS":
                        maxJobTimeout = moment.duration(task.repeating.period.each, 'days').asMilliseconds();
                        break;
                    case "HOURS":
                        maxJobTimeout = moment.duration(task.repeating.period.each, 'hours').asMilliseconds();
                        break;
                    case "MINUTES":
                        maxJobTimeout = moment.duration(task.repeating.period.each, 'minutes').asMilliseconds();
                        break;
                }
                if (typeof jobTimeout !== "undefined" && typeof jobTimeout.delayed === "number") {
                    if (jobTimeout.delayed >= maxJobTimeout) {
                        errors.push("You can not execute an operation with a job timeout greater than the repetition period.");
                    }
                } else {
                    jobTimeout = moment.duration(maxJobTimeout, 'milliseconds').asMinutes() - 1;
                    console.info("Not specified the job timeout. By default, timeout will be " + jobTimeout + " minutes");
                    this.withJobTimeout(jobTimeout);
                }

            }
        }

        if (errors.length > 0) {
            this._build = _build;
            throw errors;
        }

        if (typeof this._build.schedule.stop === "undefined") {
            console.info("Not specified the job timeout. By default, timeout will be 30 days");
            this.withJobTimeout(DEFAULT_DELAYED_STOP);
        }

        if (typeof this._build.task !== "undefined") {
            resource = this._resourcesAvailables.task;
            postObj = this._convertToTask(this._build);
        } else {
            resource = this._resourcesAvailables.job;
            postObj = this._convertToJob(this._build);
        }

        if (typeof this._build.target !== "undefined" && typeof this._build.target.filter !== "undefined") {
            resource = resource + '?entityType=' + this._entityTypeWhenFilter;
        }

        let op = new Operation(this._ogapi, resource, postObj);
        // Se deshacen todos los por defectos aplicados al objeto builder, para no condicionar el siguiente .build 
        this._build = _build;
        return op;

    }

    _convertToTask(_build) {
        let task = _build.task;
        this.executeImmediately();
        let jobObj = this._convertToJob(this._build);
        let now = moment(new Date());
        let start = moment(task.start);
        let taskObj = {
            task: {
                active: true,
                name: task.name,
                description: task.description,
                job: jobObj.job,
                schedule: {
                    start: {
                        date: start.format(DATE_FORMAT)
                    },
                    repeating: task.repeating
                }
            }
        };
        if (typeof task.stop !== "undefined") {
            if (typeof task.stop.date !== "undefined") {
                taskObj.task.schedule.stop = {
                    date: moment(task.stop.date).format(DATE_FORMAT)
                };
            } else {
                taskObj.task.schedule.stop = task.stop;
            }
        }
        if (moment.max(now, start) == now) {
            if (typeof task.stop !== "undefined" && typeof task.stop.date !== "undefined") {
                let stopDate = moment(task.stop.date);
                if (moment.max(now, stopDate) == now) {
                    throw new Error("Can not create operation object because stop operation period is earlier than current date. " +
                        "It happened because you passed a lot of time between configuration of an operation and create the operation.");
                }
            }
            delete taskObj.task.schedule.start;
        }
        return taskObj;
    }

    _convertToJob(_build) {
        if (_build.operationParameters.ackTimeout === 0) {
            delete _build.operationParameters.ackTimeout;
        }
        return {
            job: {
                request: _build
            }
        };
    }

    _addSpecificParameter(value, paramName) {
        this._build.operationParameters[paramName] = value;
    }

    _checkParam(value, configParam) {
        if (configParam.type === "number") {
            if (typeof value !== "number")
                throw new Error(configParam.name + ": Expected number but found " + typeof value);
        }

        for (let attr in configParam.attributes) {
            if (typeof VALIDATE[attr] === "function") {
                VALIDATE[attr].call(configParam.attributes[attr], value);
            }
        }
    }

    _checkMandatoryParameters() {
        if (this._config.parameters && this._config.parameters.schema) {
            const validate = this._ajv.compile(this._config.parameters.schema)
            const valid = validate(this._build.parameters)
            if(!valid) {
                throw new Error(validate.errors)
            }
        }
    }
}

'use strict';

import q from 'q';

/** 
 * This is a abstract class, it must be extended to another class that defined the specific search.
 * This class is responsible to manage execute operations request to OpenGate North API
 */
export default class Operation {
    /**
     * @param {!InternalOpenGateAPI} ogapi - this is ogapi instance
     * @param {!string} resource - this is a base url resource
     * @param {!object} postObj - it will be sent as a data on post action 
     */
    constructor(ogapi, resource, postObj) {
        this._ogapi = ogapi;
        this._resource = 'operation' + resource;
        this._postObj = postObj;
    }

    /**
     * This invoke a request to OpenGate North API and the callback is managed by promises
     * @return {Promise}
     * @property {function (result:object, statusCode:number)} then - When request it is OK
     * @property {function (error:string)} catch - When request it is NOK
     */
    updatePeriodicity() {
        let defered = q.defer();
        let id = this._resource.substring(this._resource.lastIndexOf("/") + 1);
        this._ogapi.Napi
            .put(this._resource, this._postObj)
            .then((response) => {
                let data;
                try {
                    data = JSON.parse(response.text);
                } catch (err) {
                    console.warn("Error parsing response data when execute post action to " + this._resource);
                }
                defered.resolve({
                    data: data ? data : {},
                    statusCode: response.statusCode,
                    id: id
                });
            })
            .catch((error) => {
                if (!error.data) {
                    error.data = {};
                }
                if (!error.data.errors) {
                    error.data.errors = [(typeof (error) === "string") ? {
                        message: error
                    } : error];
                }
                defered.reject(error);
            });
        return defered.promise;
    }

    /**
     * This invoke a request to OpenGate North API and the callback is managed by promises
     * @return {Promise}
     * @property {function (result:object, statusCode:number)} then - When request it is OK
     * @property {function (error:string)} catch - When request it is NOK
     */
    execute() {
        let defered = q.defer();
        this._ogapi.Napi
            .post(this._resource, this._postObj)
            .then((response) => {
                let data;
                try {
                    data = JSON.parse(response.text);
                } catch (err) {
                    console.warn("Error parsing response data when execute post action to " + this._resource);
                }
                defered.resolve({
                    data: data ? data : {},
                    statusCode: response.statusCode,
                    location: response.header.location
                });
            })
            .catch((error) => {
                if (!error.data) {
                    error.data = {};
                }
                if (!error.data.errors) {
                    error.data.errors = [(typeof (error) === "string") ? {
                        message: error
                    } : error];
                }
                defered.reject(error);
            });
        return defered.promise;
    }
}

'use strict';

import moment from 'moment';
/**
* This class generate a builder by a dynamic content about specific parameter to an operation.
*/
export default class BaseParameterBuilderWithParent{
	/**
	* @param {!InternalOpenGateAPI} ogapi - this is configuration about Opengate North API.
	* @param {!object} config - this is configuration about parameter operation.
	* @param {!BaseOperationBuilder} parent - this is a instance of BaseOperationBuilder
	*/
	constructor(ogapi,config,parent){
		this._parent = parent;
		this._build = (config.type === "array") ? [] : undefined;
		this._config = config;		
		this[createSetterNameMethod(config)] = this._appendParameterValue;
		// Ejemplo config.type=array and config.name = apn, return "addApn"
		// Ejemplo config.type=anyString and config.name = apn, return "withApn"
		function createSetterNameMethod(config){	
			let prefixOptions = (function(config){
				let prefix;
				switch(config.type){
					case 'array':
						prefix = 'add';
						break;
					default:
						prefix = 'with';
						break;
				}
				return prefix;
			})(config);

			return prefixOptions+config.name[0].toUpperCase()+config.name.slice(1);
		}
	}

	_appendParameterValue(value){
		if (!this._isTypeValueAllow(value)){			
			console.warn("Parameter <'"+this._config.name+"'> not allow type of value setted <'"+
				typeof value+"'>. Only are allowed this type: "+this._getType());	
			return this;
		}
		if (! this._isAllowValue(value)){
			console.warn("Parameter <'"+this._config.name+"'> not allow value setted <'"+
				value+"'>. Only are allowed this configuration:"+JSON.stringify(this._config.attributes));			
			return this;
		}
		if (this._config.type === "array"){
			this._build.push(value);
		}else{
			this._build = value;
		}		
		return this;
	}

	_isString(){
		return  (this._config.type === "string" || this._config.subtype === "string");
	}
	_isObject(){
		return (this._config.type === "object" || this._config.subtype === "object");
	}
	_isNumber(){
		return (this._config.type === "number" || this._config.subtype === "number");
	}
	_isArray(){
		return (this._config.type === "array" || this._config.subtype === "array");
	}
	_isBoolean(){
		return (this._config.type === "boolean" || this._config.subtype === "boolean");
	}	
	_isDate(){
		return (this._config.type === "date" || this._config.subtype === "date");
	}
	_isTime(){
		return (this._config.type === "time" || this._config.subtype === "time");	
	}

	_getType(){		
		if (this._isArray()){
			return "array";
		}
		if (this._isString() || this._isDate() || this._isTime()){
			return "string";
		}
		if (this._isObject()){
			return "object";
		}
		if (this._isNumber()){
			return "number";
		}		
		if (this._isBoolean()){
			return "boolean";
		}
		return "unknown";
	}

	_isTypeValueAllow(value){
		let isAllowed = true;
		let type = typeof value;
		if ((this._isString() || this._isTime() || this._isDate())  && type !== "string"){
			isAllowed = false;
		}
		if (this._isNumber() && type !== "number"){
			isAllowed = false;
		}
		if (this._isObject() && type !== "object"){
			isAllowed = false;
		}		
		if (this._isBoolean() && type !== "boolean"){
			isAllowed = false;
		}		
		return isAllowed;
	}

	_isAllowValue(value){
		if (typeof this._config.attributes !== "undefined"){
			let attrs = this._config.attributes;
			if (typeof attrs.list !== "undefined"){
				return typeof attrs.list.find(function(item){ return item == this;},value) !== "undefined";
			}
			/*if (typeof attrs.validator !== "undefined"){
				let format = attrs.validator;
				let isValid = moment(value,format,true).format()
				if (isValid === "Invalid date"){
					return false;
				}
			}*/
		}
		return true;
	}

	/**
	* This has all knowledge to make a object.
	* @return {object}
	*/
	build(){
		if (this._config.required && (typeof this._build === "undefined" || this._build.length === 0)) 
			throw new Error("Parameter <'"+this._config.name+"'> is required and their value is not defined");
		let parameter = {
				name: this._config.name,
				value:{
					[this._getType()]:this._build
				}
			};
		if (typeof this._build === "undefined" || this._build.length === 0){		
			parameter = undefined;
		}
		return parameter;
	}

	/**
	* This method will invoke build and then it will append the parameter to operationBuilder with the correct way
	* @return {BaseOperationBuilder}
	*/
	buildAndAppend(){		
		let paramObject = this.build();
		if (typeof paramObject !== "undefined"){
			let index = this._parent._build.parameters.findIndex(function(param){
				return param.name == this.name;
			},paramObject);
			if (index === -1){
				this._parent._build.parameters.push(paramObject);
			}else{
				this._parent._build.parameters[index] = paramObject;
			}			
		}
		return this._parent;
	}
}

