};
const _getCustomSchema = function(_ds, schema) {
    let result
    const ds = _ds[0]
    if(!ds || !schema.properties || !schema.properties[ds]){
        result =  schema
    } else{
        result = _getCustomSchema(_ds.slice(1), schema.properties[ds])
