define(function () {
	mocha.setup('bdd');
	/** @test {InternalOpenGateAPI#datapointsSearchBuilder} */
	describe('Check funcionality datapointsSearchBuilder module:', function () {
		it('Create new datapointsSearchBuilder object', function () {
